export default function Bookmarks() {
  const { user, isLoading: authLoading } = useAuth();
  const [, setLocation] = useLocation();

  const { data: bookmarks, isLoading: bookmarksLoading } = useQuery({
    queryKey: ["/api/social/bookmarks"],
